package com.etouch.taf.core.config;

// TODO: Auto-generated Javadoc
/**
 * The Class AppConfig.
 */
public class AppConfig {
	
	/** The app path. -iOS*/
	private String appPath;
	
	/** The app activity.-Android */
	private String appActivity;
	
	/** The app package.-Android */
	private String appPackage;
	
	/** The bundle id. -iOS*/
	private String bundleId;
	
	/**
	 * Gets the app path.
	 *
	 * @return the app path
	 */
	public String getAppPath() {
		return appPath;
	}
	
	/**
	 * Sets the app path.
	 *
	 * @param appPath the new app path
	 */
	public void setAppPath(String appPath) {
		this.appPath = appPath;
	}
	
	/**
	 * Gets the app activity.
	 *
	 * @return the app activity
	 */
	public String getAppActivity() {
		return appActivity;
	}
	
	/**
	 * Sets the app activity.
	 *
	 * @param appActivity the new app activity
	 */
	public void setAppActivity(String appActivity) {
		this.appActivity = appActivity;
	}
	
	/**
	 * Gets the app package.
	 *
	 * @return the app package
	 */
	public String getAppPackage() {
		return appPackage;
	}
	
	/**
	 * Sets the app package.
	 *
	 * @param appPackage the new app package
	 */
	public void setAppPackage(String appPackage) {
		this.appPackage = appPackage;
	}
	
	/**
	 * Gets the bundle id.
	 *
	 * @return the bundle id
	 */
	public String getBundleId() {
		return bundleId;
	}
	
	/**
	 * Sets the bundle id.
	 *
	 * @param bundleId the new bundle id
	 */
	public void setBundleId(String bundleId) {
		this.bundleId = bundleId;
	}
	
	
	
	

}
